package stop.one.soundhearingaid.trainer;

import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.os.Handler;
import android.util.Base64;
import android.view.View;
import android.view.animation.Animation;
import android.view.animation.AnimationUtils;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.appcompat.app.AppCompatActivity;

import pl.droidsonroids.gif.GifImageView;
import stop.one.soundhearingaid.R;

public class AnalyzerActivity extends AppCompatActivity {
    TextView text;
    ImageView think;
    GifImageView threestars, starsappear;
    ImageView graph;
    String imageString;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_analyzer);
        graph = findViewById(R.id.graph);
        text = findViewById(R.id.text);
        think = findViewById(R.id.think);
        threestars = findViewById(R.id.threestars);
        starsappear = findViewById(R.id.starsappear);

        imageString = "";
        //decode base64 string to image
        byte[] imageBytes = Base64.decode(imageString, Base64.DEFAULT);
        Bitmap decodedImage = BitmapFactory.decodeByteArray(imageBytes, 0, imageBytes.length);
        graph.setImageBitmap(decodedImage);

        new Handler().postDelayed(new Runnable() {
            @Override
            public void run() {
                Animation animationi = AnimationUtils.loadAnimation(getApplicationContext(),
                        R.anim.fade_in);
                threestars.setVisibility(View.VISIBLE);
                think.setVisibility(View.GONE);
                starsappear.setVisibility(View.GONE);
                text.setVisibility(View.GONE);

                threestars.startAnimation(animationi);

            }
        },2000);
    }
}
